    //   `0x4e23bD07d39334D804c503a35664c981004A0745`,
    //   toWei(amountTokenA)
    // );
    const receipt = await data.wait();
    const receipt1 = await data.wait();
    console.log("liquidity provider..", receipt1);
    console.log("liquidity provider..", receipt);
    return receipt;
  } catch (error) {
    console.log("error in Adding liquidity provider amount..", error);
  }
};
