import React, { useState } from "react";

function Modal({ setModal, setDestToken, setSrcToken, modal }) {
  const coins = ["CoinA", "Eth", "CoinB", "CoinC"];
  const [selectedCoin, setSelectedCoin] = useState("");

  const handleRadioChange = (coin) => {
    if (modal) {
      setSelectedCoin(coin);
      setDestToken(coin);
    } else {
      setSelectedCoin(coin);
      setSrcToken(coin);
    }
  };

  return (
    <>
      <div className="h-screen w-full fixed left-0 top-0 flex justify-center items-center bg-black bg-opacity-50 z-20">
        {/* Modal */}
        <div className="bg-white dark:bg-gray-800 rounded-xl shadow-lg w-[20%]">
          {/* modal header */}
          <div className="border-b- border-solid border-gray px-4 py-4 text-center text-white">
            <h3 className="text-xl font-bold">Select A Token</h3>
          </div>
          {/* modal body */}
          <div className="p-5">
            {/* Radio buttons for Coins */}
            {coins.map((coin) => (
              <div key={coin} className="flex items-center mb-3">
                <input
                  type="radio"
                  id={coin}
                  value={coin}
                  checked={selectedCoin === coin}
                  onChange={() => handleRadioChange(coin)}
                  className="transform scale-150 mr-4"
                />
                <label className="text-xl text-white" htmlFor={coin}>
                  {coin}
                </label>
              </div>
            ))}
          </div>
          <div className="flex justify-end items-center w-100 border-t p-4">
            <button
              onClick={() => setModal(false)}
              className="bg-red-600 hover:bg-red-700 px-5 py-2 rounded-lg text-lg text-white mr-3"
            >
              cancle
            </button>
            <button
              onClick={() => setModal(false)}
              className="bg-blue-600 hover:bg-blue-700 px-5 py-2 text-lg rounded-lg text-white mr-1"
            >
              okay
            </button>
          </div>
        </div>
      </div>
    </>
  );
}

export default Modal;
