import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

const defaultToastOptions = {
  position: toast.POSITION.TOP_RIGHT,
  autoClose: 3000, // 3 seconds, adjust as needed
};

const Toast = {
  success: (message) => {
    toast.success(message, {
      ...defaultToastOptions,
      style: { background: "#4CAF50", color: "#fff" }, // Green background
    });
  },
  error: (message) => {
    toast.error(message, {
      ...defaultToastOptions,
      style: { background: "#f44336", color: "#fffff" }, // Red background
    });
  },
  warning: (message) => {
    toast.warning(message, {
      ...defaultToastOptions,
      style: { background: "#ff9800", color: "#ffffff" }, // Orange background
    });
  },
};

const ToastComponent = () => {
  return <ToastContainer />;
};

export { Toast, ToastComponent };
