    const data = await tokenContractObj.approve(
      `0x343909a9B02A32e248fABB0e37428921Eac26CD1`,
      toWei(amount)
    );
    const receipt = await data.wait();
    return receipt;
  } catch (error) {
    console.log("error", error);
  }
};


