import React, { useEffect, useState } from "react";
import { useAccount } from "wagmi";
import { TransactionRecord } from "../utils/transactionRecord";

export default function Token() {
  const { address } = useAccount();
  const [state, setState] = useState([]);

  useEffect(() => {
    const fetchToken = async () => {
      if (address) {
        const data = await TransactionRecord();
        setState(data);
      }
    };
    fetchToken();
  }, [address]);

  console.log("state", state);

  return (
    <>
      <div className="w-full dark:bg-gray-700 py-32">
        <table className="table-fixed border border-slate-600 w-9/12 m-auto">
          <thead className="">
            <tr className="border border-slate-600">
              <th className="text-[#ffffff] text-xl p-4">Receiver Address</th>
              <th className="text-[#ffffff] text-xl p-4">Token Name</th>
              <th className="text-[#ffffff] text-xl p-4">Amount</th>
              <th className="text-[#ffffff] text-xl p-4">Time</th>
            </tr>
          </thead>
          {state &&
            state?.map((el, i) => (
              // <div
              //   key={i}
              //   className={`w-full m-auto flex items-center cursor-pointer p-1 mb-3 rounded-2xl
              //          "bg-blue-500 text-white"
              //          "bg-gray-200"
              //     `}
              // >
              <tbody key={i} className="hover:bg-blue-500 text-white ">
                <tr className="">
                  <td className="text-center text-[#ffffff] text-md p-4">
                    {el._receiver}
                  </td>
                  <td className="text-center text-[#ffffff] text-md p-4">
                    {el.token}
                  </td>
                  <td className="text-center text-[#ffffff] text-md p-4">
                    {el.Amount}
                  </td>
                  <td className="text-center text-[#ffffff] text-md p-4">
                    {el.timestamp}
                  </td>
                </tr>
              </tbody>
            ))}{" "}
        </table>
      </div>
    </>
  );
}
