import React, { useEffect, useState } from "react";
import { useAccount } from "wagmi";
import { TransactionRecord } from "../utils/transactionRecord";

export default function Token() {
  const { address } = useAccount();
  const [state, setState] = useState([]);

  useEffect(() => {
    const fetchToken = async () => {
      if (address) {
        const data = await TransactionRecord();
        setState(data);
      }
    };
    fetchToken();
  }, [address]);

  console.log("state", state);

  return (
    <>
      <div className="w-full dark:bg-gray-700 pt-20 pb-80">
        <p className="text-white text-4xl hover:dark:text-blue-700 text-center font-semibold mb-20">
          Transaction Records
        </p>
        <table className="table-fixed border border-slate-600 w-9/12 m-auto pb-20">
          <thead className="dark:bg-blue-700">
            <tr className="border border-slate-600">
              <th className="text-[#ffffff] text-xl p-4">Receiver Address</th>
              <th className="text-[#ffffff] text-xl p-4">Receiced Token</th>
              <th className="text-[#ffffff] text-xl p-4">Amount</th>
              <th className="text-[#ffffff] text-xl p-4">Time</th>
            </tr>
          </thead>
          {state &&
            state?.map((el, i) => (
              <tbody
                key={i}
                className="hover:dark:bg-gray-900 focus:ring-4 focus:outline-none focus:ring-blue-300 text-white cursor-pointer"
              >
                <tr className="">
                  <td className="text-center text-[#ffffff] text-md p-4">
                    {el._receiver}
                  </td>
                  <td className="text-center text-[#ffffff] text-md p-4">
                    {el.token}
                  </td>
                  <td className="text-center text-[#ffffff] text-md p-4">
                    {el.Amount}
                  </td>
                  <td className="text-center text-[#ffffff] text-md p-4">
                    {el.timestamp}
                  </td>
                </tr>
              </tbody>
            ))}{" "}
        </table>
      </div>
    </>
  );
}
