import { ethers } from "ethers";
import CustomTokenAbi from "../abi/CustomToken.json";
import UniswapAbi from "../abi/Uniswap.json";
//here pool import..
import LiquidityPool from "../abi/LiquidityPool.json";
import tokenAbi from "../abi/ERC20Token.json";

export const tokenContract = async (address) => {
  const provider = new ethers.providers.Web3Provider(window.ethereum);
  const { ethereum } = window;
  if (ethereum) {
    const signer = provider.getSigner();
    const contractReader = new ethers.Contract(
      address,
      CustomTokenAbi.abi,
      signer
    );
    return contractReader;
  }
};

export const contract = async () => {
  const provider = new ethers.providers.Web3Provider(window.ethereum);
  const { ethereum } = window;
  if (ethereum) {
    const signer = provider.getSigner();
    const contractReader = new ethers.Contract(
      `0x8dA78e6D47870911bE47A711d24c7946d055b8Cc`,
      UniswapAbi.abi,
      signer
    );
    return contractReader;
  }
};

//pool code here...
export const poolcontract = async () => {
  const provider = new ethers.providers.Web3Provider(window.ethereum);
  const { ethereum } = window;
  if (ethereum) {
    const signer = provider.getSigner();
    const contractReader = new ethers.Contract(
      `0xD8866f90F5A3E85aAE9af94ef339912af18cFf1F`,
      LiquidityPool.abi,
      signer
    );
    return contractReader;
  }
};

//here token code..
export const tokenAContract = async () => {
  const provider = new ethers.providers.Web3Provider(window.ethereum);
  const { ethereum } = window;
  if (ethereum) {
    const signer = provider.getSigner();
    const contractReader = new ethers.Contract(
      `0x08D2e379323b4D5E0a2C6BA18F082902c4CE4a66`,
      tokenAbi.abi,
      signer
    );
    return contractReader;
  }
};
