import { poolcontract } from "./contract";
import { tokenAContract } from "./contract";
import { toWei } from "./ether-utils";

export const CreateLiquidityPool = async (
  name,
  symbol,
  tokenAddressA,
  tokenAddressB
) => {
  try {
    const contractPoolObj = await poolcontract();
    const data = await contractPoolObj.createLiquidityPool(
      name,
      symbol,
      tokenAddressA,
      tokenAddressB
    );
    const receipt = await data.wait();
    return receipt;
  } catch (error) {
    console.log("error in createliquidityPool..", error);
  }
};

export const AddLiquidityAmount = async (
  amountTokenA,
  amountTokenB,
  tokenA,
  tokenB
) => {
  try {
    const contractPoolObj = await poolcontract();
    const tokenContractObj = await contractPoolObj.addLiquidity(
      amountTokenA,
      amountTokenB,
      tokenA,
      tokenB
    );
    const tokenContractData = await tokenContractObj.wait();
    console.log("liquidity provider Amount added..", tokenContractData);
    return tokenContractData;
  } catch (error) {
    console.log("error in Adding liquidity provider amount..", error);
  }
};

//here tokenA approval function..
export const TokenAapprove = async (amountTokenA) => {
  try {
    const tokenApprove = await tokenAContract();
    const data = await tokenApprove.approve(
      `0x7171c4a932c4F5Ba5A00b8e822d38ffCac44A1C1`,
      toWei(amountTokenA)
    );
    const receipt = await data.wait();
    console.log("Approve tokenA done..", receipt);
    return receipt;
  } catch (error) {
    console.log("error in Approving tokenA..", error);
  }
};

//here tokenB approval function..
export const TokenBAapprove = async (amountTokenB) => {
  try {
