    const data = await tokenApprove.approve(
      `0xE6c1B86b9a3853573e953654D284F64219D59f56`,
      toWei(amountTokenB)
    );
    const receipt = await data.wait();
    console.log("Approve tokenB done..", receipt);
    return receipt;
  } catch (error) {
    console.log("error in Approving tokenB..", error);
  }
};

//here we getting the reserve token.
export const ReserveTokenA = async () => {
  try {
    const contractPoolObj = await poolcontract();
    const tokenContractObj = await contractPoolObj.reserveTokenA();
    const reserveTokenA = await tokenContractObj.wait();
    console.log("liquidity provider Amount added..", reserveTokenA);
    return reserveTokenA;
  } catch (error) {
    console.log("error in getting the reserve tokenA..", error);
  }
};

//here we getting the reserve token.
export const ReserveTokenB = async () => {
  try {
    const contractPoolObj = await poolcontract();
