import { contract } from "./contract";
import { toEth, toWei } from "./ether-utils";

export const TransactionRecord = async () => {
  try {
    const TransactionObj = await contract();
    const count = await TransactionObj.transactionCount();
    const newCount = Number(toWei(count.toString()));
    //console.log("count......", Number(newCount) / 1000000000000000000);
    let transactionArray = [];
    for (let i = 0; i < count.length; i++) {
      const data = TransactionObj.transaction(i);
      transactionArray.push(data);
    }
    return transactionArray;
  } catch (error) {
    console.log(error);
  }
};
