import React from "react";

function AddLiquidityModel() {
  return (
    <>
      <div className="fixed left-0 top-0 w-full h-screen flex items-center justify-center bg-black bg-opacity-50 z-20">
        <div className="bg-white p-3 text-white dark:bg-gray-900 rounded-xl shadow-lg w-[30%]">
          <p className="text-center text-xl"> Add Liquidity</p>
          <div className="border-t mt-5 border-gray-800">
            <p className="mt-4">Select Pair</p>
            <div className="relative">
              <input
                value={
                  destToken !== "Select a token" && srcToken === "Eth"
                    ? inputValue * 10000
                    : destToken === "Eth" && srcToken !== "Eth"
                    ? inputValue / 10000
                    : destToken === "CoinB" && srcToken !== "Eth"
                    ? inputValue * 2
                    : destToken !== "Eth" && srcToken === "CoinB"
                    ? inputValue / 2
                    : inputValue
                }
                placeholder={"0.0"}
                onChange={(e) => {
                  setOutputValue(e.target.value);
                }}
                type="text"
                className="w-full border-none text-white bg-white dark:bg-gray-700 text-3xl border-2 border-grey-500 py-8 px-4 rounded-2xl focus:outline-none"
              />
              <p className="absolute top-2 left-3 text-white text-sm">
                You receive
              </p>
              <button
                onClick={handleModal}
                className="flex justify-center items-center gap-1 absolute text-white top-[25%] right-4  bg-white dark:bg-gray-800 font-medium rounded-3xl text-lg px-4 py-2 text-center mr-3 md:mr-0"
              >
                {destToken === srcToken ? "Select a token" : destToken}{" "}
                <IoIosArrowDown />
              </button>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default AddLiquidityModel;
